function check(){document.querySelectorAll(".cart__input").forEach(t=>{""==t.querySelector("input").value&&"nn"!=t.id?t.classList.toggle("nessesary"):(t.classList.remove("nessesary"),document.location.href="")})}var checkbox=document.querySelector(".cart__agreement").querySelector("input");checkbox.addEventListener("click",t=>{"on"==checkbox.value?document.querySelector(".cart__button").outerHTML='<button onclick="check()" class="cart__button button">Order</button>':document.querySelector(".cart__button").outerHTML='<button  class="cart__button button">Order</button>'});